import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ClaseDetalleAlumno.css';

const ClaseDetalleAlumno = () => {
  const { id } = useParams();
  const [clase, setClase] = useState(null);
  const [avisos, setAvisos] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const claseRes = await axios.get(`http://localhost:3001/api/clases/${id}`);
        setClase(claseRes.data);

        const avisosRes = await axios.get(`/api/avisos/clase/${id}`);
        setAvisos(avisosRes.data);
      } catch (err) {
        console.error('Error al cargar datos:', err);
      }
    };

    fetchData();
  }, [id]);

  if (!clase) return <div className="cargando">Cargando clase...</div>;

  return (
    <div className="clase-detalle-alumno">
      <h2 className="titulo-clase">{clase.nombre} - {clase.codigo_grupo}</h2>

      <div className="contenido-clase">
        {/* Avisos */}
        <div className="avisos">
          <h3>Avisos del maestro</h3>
          {avisos.length === 0 && <p>No hay avisos aún.</p>}
          {avisos.map(aviso => (
            <div key={aviso.id} className="aviso">
              <p className="aviso-contenido">{aviso.texto}</p>
              <small>{new Date(aviso.fecha).toLocaleString()}</small>

              {aviso.archivos?.length > 0 && (
                <div className="archivos">
                  <strong>Archivos adjuntos:</strong>
                  <div className="adjuntos-grid">
                    {aviso.archivos.map((archivo, index) => {
                      const ruta = `/storage/${archivo.ruta_archivo}`;
                      const isImage = archivo.ruta_archivo.match(/\.(jpg|jpeg|png|gif)$/i);
                      const isPDF = archivo.ruta_archivo.match(/\.pdf$/i);
                      return (
                        <div key={index} className="adjunto-card">
                          {isImage ? (
                            <img src={ruta} alt={archivo.nombre_archivo} />
                          ) : isPDF ? (
                            <embed src={ruta} type="application/pdf" width="100%" height="200px" />
                          ) : (
                            <p>{archivo.nombre_archivo}</p>
                          )}
                          <a href={ruta} target="_blank" rel="noopener noreferrer">
                            Ver archivo
                          </a>
                        </div>
                      );
                    })}
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Participantes */}
        <div className="participantes">
          <div className="viñeta maestro">
            <h4>👨‍🏫 Maestro</h4>
            <p>{clase.maestro?.nombre}</p>
          </div>
          <div className="viñeta alumnos">
            <h4>👥 Compañeros</h4>
            <ul>
              {clase.alumnos.map(alumno => (
                <li key={alumno.id}>{alumno.nombre}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ClaseDetalleAlumno;
