import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './TareaDetalleMaestro.css';

const TareaDetalleMaestro = () => {
  const { idClase, idTarea } = useParams(); // ya que ahora recibes los dos
  const { usuario } = useAuth();
  const [tarea, setTarea] = useState(null);
  const [entregas, setEntregas] = useState([]);

  useEffect(() => {
    const fetchTarea = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/api/avisos/${idTarea}`);
        setTarea(res.data);
      } catch (err) {
        console.error('Error al cargar la tarea:', err);
      }
    };

    const fetchEntregas = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/api/tareas/${idTarea}/entregas`, {
          headers: { Authorization: `Bearer ${usuario.token}` }
        });
        setEntregas(res.data);
      } catch (err) {
        console.error('Error al cargar entregas:', err);
      }
    };

    fetchTarea();
    fetchEntregas();
  }, [idTarea, usuario]);

  const calificarEntrega = async (idEntrega, calificacion) => {
    try {
      await axios.put(`http://localhost:3001/api/entregas/${idEntrega}`, { calificacion }, {
        headers: { Authorization: `Bearer ${usuario.token}` }
      });
      setEntregas(prev => prev.map(e => e.id === idEntrega ? { ...e, calificacion } : e));
    } catch (err) {
      alert('Error al guardar calificaciÃ³n.');
    }
  };

  if (!tarea) return <p>Cargando tarea...</p>;

  return (
    <div className="tarea-detalle">
      <h2>ð {tarea.texto}</h2>

      <div className="tarea-info">
        <p><strong>Fecha de publicaciÃ³n:</strong> {new Date(tarea.fecha).toLocaleString()}</p>
        {tarea.fecha_entrega && (
          <p><strong>Fecha de entrega:</strong> {new Date(tarea.fecha_entrega).toLocaleDateString()}</p>
        )}

        <hr />

        <h3>ð DescripciÃ³n</h3>
        <p style={{ whiteSpace: 'pre-wrap' }}>{tarea.texto}</p>

        {tarea.archivos?.length > 0 && (
          <div>
            <h4>ð Archivos adjuntos</h4>
            <ul>
              {tarea.archivos.map((ruta, i) => (
                <li key={i}>
                  <a
                    href={`http://localhost:3001/storage/${ruta}`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    Ver archivo {i + 1}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      <hr />

      <h3>ð Entregas de alumnos</h3>
      {entregas.length === 0 ? (
        <p>No hay entregas registradas.</p>
      ) : (
        <table className="tabla-entregas">
          <thead>
            <tr>
              <th>Alumno</th>
              <th>MatrÃ­cula</th>
              <th>Archivo</th>
              <th>Fecha de entrega</th>
              <th>CalificaciÃ³n</th>
            </tr>
          </thead>
          <tbody>
            {entregas.map(entrega => (
              <tr key={entrega.id_alumno}>
                <td>{entrega.nombre_alumno}</td>
                <td>{entrega.matricula}</td>
                <td>
                  {entrega.archivo ? (
                    <a
                      href={`http://localhost:3001/storage/${entrega.archivo}`}
                      target="_blank"
                      rel="noreferrer"
                    >
                      Ver archivo
                    </a>
                  ) : (
                    <span style={{ color: 'gray' }}>Sin entrega</span>
                  )}
                </td>
                <td>
                  {entrega.fecha_entrega ? (
                    new Date(entrega.fecha_entrega).toLocaleString()
                  ) : (
                    '-'
                  )}
                </td>
                <td>
                  {entrega.archivo ? (
                    <input
                      type="number"
                      min="0"
                      max="100"
                      value={entrega.calificacion || ''}
                      onChange={e =>
                        calificarEntrega(entrega.id_entrega, e.target.value)
                      }
                    />
                  ) : (
                    <span style={{ color: 'gray' }}>N/A</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>);
};

export default TareaDetalleMaestro;
