import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './TareaDetalleAlumno.css';

const TareaDetalleAlumno = () => {
  const { idClase, idTarea } = useParams();
  const { usuario } = useAuth();
  const navigate = useNavigate();
  const [tarea, setTarea] = useState(null);
  const [entregas, setEntregas] = useState([]);
  const [archivosNuevos, setArchivosNuevos] = useState([]);

  useEffect(() => {
    const fetchTareaYEntregas = async () => {
      try {
        const resTarea = await axios.get(`http://localhost:3001/api/avisos/${idTarea}`);
        setTarea(resTarea.data);

        const resEntregas = await axios.get(`http://localhost:3001/api/entregas/alumno/${usuario.id}/entregas`);
        const filtradas = resEntregas.data.filter(e => e.id_tarea === parseInt(idTarea));
        setEntregas(filtradas);
      } catch (err) {
        console.error('Error al cargar tarea o entregas:', err);
      }
    };

    fetchTareaYEntregas();
  }, [idTarea, usuario.id]);

  const subirArchivos = async () => {
    if (!archivosNuevos.length) return alert('Selecciona al menos un archivo.');
    const formData = new FormData();
    formData.append('id_tarea', idTarea);
    formData.append('id_alumno', usuario.id);
    archivosNuevos.forEach(file => formData.append('archivos', file));

    try {
      await axios.post('http://localhost:3001/api/entregas', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setArchivosNuevos([]);
      const res = await axios.get(`http://localhost:3001/api/entregas/alumno/${usuario.id}/entregas`);
      const nuevas = res.data.filter(e => e.id_tarea === parseInt(idTarea));
      setEntregas(nuevas);
    } catch (err) {
      console.error('Error al subir archivos:', err);
    }
  };

  const eliminarEntrega = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/entregas/${id}`);
      const res = await axios.get(`http://localhost:3001/api/entregas/alumno/${usuario.id}/entregas`);
      const nuevas = res.data.filter(e => e.id_tarea === parseInt(idTarea));
      setEntregas(nuevas);
    } catch (err) {
      console.error('Error al eliminar entrega:', err);
    }
  };

  const confirmarEntrega = async () => {
    try {
      await axios.post('http://localhost:3001/api/entregas/confirmar', {
        id_tarea: idTarea,
        id_alumno: usuario.id
      });
      const res = await axios.get(`http://localhost:3001/api/entregas/alumno/${usuario.id}/entregas`);
      const nuevas = res.data.filter(e => e.id_tarea === parseInt(idTarea));
      setEntregas(nuevas);
    } catch (err) {
      console.error('Error al confirmar entrega:', err);
    }
  };

  const yaEntregado = entregas.some(e => e.entregado);
  const calificacionAsignada = entregas.find(e => e.calificacion != null)?.calificacion;

  return (
    <div className="detalle-tarea-alumno">
      <button onClick={() => navigate(-1)}>← Volver</button>
      <h2>📝 {tarea?.texto}</h2>
      {tarea?.fecha_entrega && (
        <p><strong>Fecha de entrega:</strong> {new Date(tarea.fecha_entrega).toLocaleDateString()}</p>
      )}

      {tarea?.archivos?.length > 0 && (
        <div className="adjuntos-grid">
          {tarea.archivos.map((archivo, i) => {
            const url = `http://localhost:3001/storage/${archivo}`;
            const isImage = archivo.match(/\.(jpg|jpeg|png|gif)$/i);
            const isPDF = archivo.match(/\.pdf$/i);
            return (
              <div key={i} className="adjunto-card">
                {isImage ? <img src={url} className="imagen-miniatura" /> : isPDF ? <iframe src={`${url}#toolbar=0`} className="pdf-miniatura" /> : <p>{archivo}</p>}
                <a href={url} target="_blank" rel="noreferrer">Ver</a>
              </div>
            );
          })}
        </div>
      )}

      <hr />
      <h4>Tu trabajo</h4>

      <input
        type="file"
        multiple
        onChange={(e) => setArchivosNuevos(Array.from(e.target.files))}
        accept=".pdf,image/*"
      />
      <button onClick={subirArchivos}>Subir archivos</button>

      {entregas.length > 0 && (
        <div className="adjuntos-grid">
          {entregas.map((entrega) => {
            const url = `http://localhost:3001/storage/${entrega.archivo}`;
            const isImage = entrega.archivo.match(/\.(jpg|jpeg|png|gif)$/i);
            const isPDF = entrega.archivo.match(/\.pdf$/i);
            return (
              <div key={entrega.id} className="adjunto-card">
                {isImage ? <img src={url} className="imagen-miniatura" /> : isPDF ? <iframe src={`${url}#toolbar=0`} className="pdf-miniatura" /> : <p>{entrega.archivo}</p>}
                <a href={url} target="_blank" rel="noreferrer">Ver</a>
                {!yaEntregado && <button onClick={() => eliminarEntrega(entrega.id)}>Eliminar</button>}
              </div>
            );
          })}
        </div>
      )}

      {!yaEntregado && entregas.length > 0 && (
        <button onClick={confirmarEntrega}>📤 Entregar</button>
      )}

      {calificacionAsignada != null && (
        <p><strong>Calificación:</strong> {calificacionAsignada}</p>
      )}
    </div>
  );
};

export default TareaDetalleAlumno;
