import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './AlumnoClases.css';

const AlumnoClases = () => {
  const [clases, setClases] = useState([]);
  const [tareasPorClase, setTareasPorClase] = useState({});
  const { usuario } = useAuth(); 

  useEffect(() => {
    if (!usuario?.id) return;

    const fetchClases = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/api/alumno/${usuario.id}/clases`);
        setClases(res.data);
      } catch (err) {
        console.error('Error al obtener las clases del alumno:', err);
      }
    };

    const fetchTareasProximas = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/api/alumno/${usuario.id}/tareas-proximas`);
        const agrupadas = {};

        res.data.forEach((tarea) => {
          if (!agrupadas[tarea.id_clase]) agrupadas[tarea.id_clase] = [];
          if (agrupadas[tarea.id_clase].length < 2) {
            agrupadas[tarea.id_clase].push(tarea);
          }
        });

        setTareasPorClase(agrupadas);
      } catch (err) {
        console.error('Error al obtener tareas pr칩ximas:', err);
      }
    };

    fetchClases();
    fetchTareasProximas();
  }, [usuario]);

  if (!usuario) {
    return <p>Cargando usuario...</p>;
  }

  return (
    <div className="alumno-clases">
      <h2>Mis Clases</h2>

      {clases.length === 0 ? (
        <p>No est치s inscrito en ninguna clase.</p>
      ) : (
        <div className="tarjetas-clases">
          {clases.map((clase) => (
            <div key={clase.id} className="tarjeta-contenedor">
              <Link
                to={`/alumno/clase/${clase.id}`}
                className="tarjeta-clase"
              >
                <h3>{clase.nombre}</h3>
                <p>Grupo: {clase.grupo}</p>
                <p>Cuatrimestre: {clase.cuatrimestre}</p>
                <p>Carrera: {clase.carrera}</p>
              </Link>

              {tareasPorClase[clase.id] && (
                <div className="tareas-proximas-clase">
                  <p><strong>Pr칩ximas entregas:</strong></p>
                  <ul>
                    {tareasPorClase[clase.id].map((tarea) => (
                      <li key={tarea.id}>
                        <Link to={`/alumno/clase/${clase.id}/tarea/${tarea.id}`} className="enlace-tarea">
                          游닇 {tarea.texto}
                        </Link>
                        <br />
                        <small>Entrega: {new Date(tarea.fecha_entrega).toLocaleDateString()}</small>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AlumnoClases;
