import { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './TrabajoEnClaseMaestro.css';
import { useAuth } from '../context/AuthContext';

const TrabajoEnClaseMaestro = () => {
  const { id } = useParams(); // id de clase
  const { usuario } = useAuth();
  const [temas, setTemas] = useState([]);
  const [nombreNuevoTema, setNombreNuevoTema] = useState('');
  const [editandoTemaId, setEditandoTemaId] = useState(null);
  const [nombreEditado, setNombreEditado] = useState('');
  const [avisos, setAvisos] = useState([]);
  const [busqueda, setBusqueda] = useState('');
  const [seleccionTemaId, setSeleccionTemaId] = useState(null);

  useEffect(() => {
    fetchTemas();
    fetchAvisos();
  }, [id]);

  const fetchTemas = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/temas/${id}`);
      setTemas(res.data);
    } catch (err) {
      console.error('Error al cargar temas:', err);
    }
  };

  const fetchAvisos = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/avisos/clase/${id}`);
      setAvisos(res.data);
    } catch (err) {
      console.error('Error al cargar avisos:', err);
    }
  };

  const crearTema = async () => {
    if (!nombreNuevoTema.trim()) return;
    try {
      await axios.post('http://localhost:3001/api/temas', {
        id_clase: id,
        nombre: nombreNuevoTema
      });
      setNombreNuevoTema('');
      fetchTemas();
    } catch (err) {
      console.error('Error al crear tema:', err);
    }
  };

  const actualizarTema = async (idTema) => {
    try {
      await axios.put(`http://localhost:3001/api/temas/${idTema}`, {
        nombre: nombreEditado
      });
      setEditandoTemaId(null);
      setNombreEditado('');
      fetchTemas();
    } catch (err) {
      console.error('Error al editar tema:', err);
    }
  };

  const eliminarTema = async (idTema) => {
    if (!window.confirm('¿Deseas eliminar este tema?')) return;
    try {
      await axios.delete(`http://localhost:3001/api/temas/${idTema}`);
      fetchTemas();
    } catch (err) {
      console.error('Error al eliminar tema:', err);
    }
  };

  const agregarAvisoATema = async (idAviso, idTema) => {
    try {
      await axios.post(`http://localhost:3001/api/temas/${idTema}/agregar-aviso`, { id_aviso: idAviso });
      fetchTemas();
    } catch (err) {
      console.error('Error al agregar aviso al tema:', err);
    }
  };

  const filtrarAvisos = () => {
    return avisos.filter(aviso =>
      aviso.texto.toLowerCase().includes(busqueda.toLowerCase()) &&
      !temas.some(t => t.avisos?.some(a => a.id === aviso.id))
    );
  };

  return (
    <div className="trabajo-clase">
      <h2>📂 Trabajo en Clase</h2>

      <div className="crear-tema">
        <input
          type="text"
          value={nombreNuevoTema}
          onChange={(e) => setNombreNuevoTema(e.target.value)}
          placeholder="Nuevo tema..."
        />
        <button onClick={crearTema}>➕ Crear tema</button>
      </div>

      {temas.map((tema) => (
        <div key={tema.id} className="tema-bloque">
          <div className="tema-header">
            {editandoTemaId === tema.id ? (
              <>
                <input
                  value={nombreEditado}
                  onChange={(e) => setNombreEditado(e.target.value)}
                />
                <button onClick={() => actualizarTema(tema.id)}>💾</button>
                <button onClick={() => setEditandoTemaId(null)}>❌</button>
              </>
            ) : (
              <>
                <h3>{tema.nombre}</h3>
                <div>
                  <button onClick={() => {
                    setEditandoTemaId(tema.id);
                    setNombreEditado(tema.nombre);
                  }}>✏️</button>
                  <button onClick={() => eliminarTema(tema.id)}>🗑️</button>
                  <button onClick={() => setSeleccionTemaId(tema.id)}>➕ Agregar contenido</button>
                </div>
              </>
            )}
          </div>

          <ul className="avisos-en-tema">
            {tema.avisos?.map((a) => (
              <li key={a.id}>
                <span>{a.es_tarea ? '📝' : a.es_material ? '📚' : '📢'} {a.texto}</span>
              </li>
            ))}
          </ul>
        </div>
      ))}

      {seleccionTemaId && (
        <div className="popup-agregar">
          <div className="popup-contenido">
            <h4>Agregar tareas o materiales al tema</h4>
            <input
              type="text"
              value={busqueda}
              onChange={(e) => setBusqueda(e.target.value)}
              placeholder="Buscar por título..."
            />
            <ul className="resultados-agregar">
              {filtrarAvisos().map(aviso => (
                <li key={aviso.id}>
                  <span>{aviso.es_tarea ? '📝' : aviso.es_material ? '📚' : '📢'} {aviso.texto}</span>
                  <button onClick={() => agregarAvisoATema(aviso.id, seleccionTemaId)}>Agregar</button>
                </li>
              ))}
            </ul>
            <button onClick={() => setSeleccionTemaId(null)} className="cerrar-popup">Cerrar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TrabajoEnClaseMaestro;
