import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './ClaseDetalleMaestro.css';

const ClaseDetalleMaestro = () => {
  const { id } = useParams();
  const { usuario } = useAuth();
  const [clase, setClase] = useState(null);
  const [avisos, setAvisos] = useState([]);
  const [seccion, setSeccion] = useState('avisos');

  useEffect(() => {
    axios.get(`http://localhost:3001/api/clases/${id}/detalle`)
      .then(res => setClase(res.data))
      .catch(err => console.error('Error al cargar clase:', err));
  }, [id]);

  useEffect(() => {
    if (seccion === 'avisos') {
      axios.get(`http://localhost:3001/api/avisos/clase/${id}`)
        .then(res => setAvisos(res.data))
        .catch(err => console.error('Error al cargar avisos:', err));
    }
  }, [seccion, id]);

  if (!clase) return <p>Cargando...</p>;

  return (
    <div className="clase-contenedor">
      <h2>{clase.nombre} ({clase.codigo_grupo})</h2>
      <p><strong>Carrera:</strong> {clase.carrera}</p>
      <p><strong>Cuatrimestre:</strong> {clase.cuatrimestre}</p>

      <div className="clase-tabs">
        <button onClick={() => setSeccion('avisos')}>📢 Avisos</button>
        <button onClick={() => setSeccion('personas')}>👥 Personas</button>
      </div>

      {seccion === 'avisos' && (
        <ul className="avisos-lista">
          {avisos.map(aviso => (
            <li key={aviso.id} className="aviso-item">
              <div className="aviso-header">
                <strong>{new Date(aviso.fecha).toLocaleString()}</strong>
              </div>
              <p>{aviso.texto}</p>
              {aviso.archivos && aviso.archivos.length > 0 && (
                <div className="adjuntos-grid">
                  {aviso.archivos.map((archivo, index) => {
                    const filename = typeof archivo === 'string' ? archivo : archivo.nombre_archivo;
                    const isImage = filename.match(/\.(jpg|jpeg|png|gif)$/i);
                    const isPDF = filename.match(/\.pdf$/i);
                    return (
                      <div key={index} className="adjunto-card">
                        {isImage ? (
                          <img src={`http://localhost:3001/storage/${filename}`} alt={`Archivo ${index}`} />
                        ) : isPDF ? (
                          <embed src={`http://localhost:3001/storage/${filename}`} type="application/pdf" />
                        ) : (
                          <p>Archivo {index + 1}</p>
                        )}
                        <a
                          href={`http://localhost:3001/storage/${filename}`}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          Ver archivo
                        </a>
                      </div>
                    );
                  })}
                </div>
              )}
            </li>
          ))}
        </ul>
      )}

      {seccion === 'personas' && (
        <ul>
          <li><strong>Maestro:</strong> {clase.maestro?.nombre}</li>
          <li><strong>Alumnos:</strong></li>
          {clase.alumnos?.map(al => (
            <li key={al.id}>• {al.nombre}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ClaseDetalleMaestro;
