import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './ClaseDetalleMaestro.css';

const TareaDetalleAlumno = () => {
  const { id } = useParams(); // id de la tarea
  const { usuario } = useAuth();
  const navigate = useNavigate();
  const [tarea, setTarea] = useState(null);
  const [entrega, setEntrega] = useState(null);
  const [archivosEntrega, setArchivosEntrega] = useState([]);

  useEffect(() => {
    const cargarDatos = async () => {
      try {
        const tareaRes = await axios.get(`http://localhost:3001/api/avisos/${id}`);
        setTarea(tareaRes.data);

        const entregasRes = await axios.get(`http://localhost:3001/api/entregas/alumno/${usuario.id}/entregas`, {
          headers: { Authorization: `Bearer ${usuario.token}` }
        });

        const entregaEncontrada = entregasRes.data.find(e => e.id_tarea === parseInt(id));
        setEntrega(entregaEncontrada);
      } catch (err) {
        console.error('Error al cargar tarea o entrega:', err);
      }
    };

    cargarDatos();
  }, [id, usuario]);

  const entregar = async () => {
    const formData = new FormData();
    formData.append('id_tarea', id);
    formData.append('id_alumno', usuario.id);
    archivosEntrega.forEach(file => formData.append('archivos', file));

    try {
      await axios.post('http://localhost:3001/api/entregas', formData, {
        headers: {
          Authorization: `Bearer ${usuario.token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Tarea entregada');
      window.location.reload();
    } catch (err) {
      console.error('Error al entregar:', err);
    }
  };

  const deshacerEntrega = async () => {
    if (!window.confirm('¬øSeguro que quieres deshacer tu entrega?')) return;

    try {
      await axios.delete(`http://localhost:3001/api/entregas/${entrega.id}`, {
        headers: { Authorization: `Bearer ${usuario.token}` }
      });
      setEntrega(null);
    } catch (err) {
      console.error('Error al deshacer entrega:', err);
    }
  };

  if (!tarea) return <p>Cargando...</p>;

  const fechaEntrega = tarea.fecha_entrega ? new Date(tarea.fecha_entrega) : null;
  const fechaReal = entrega?.fecha_entrega ? new Date(entrega.fecha_entrega) : null;
  const entregadoTarde = fechaEntrega && fechaReal > fechaEntrega;

  return (
    <div className="clase-contenedor">
      <button onClick={() => navigate(-1)}>‚¨Ö Volver</button>
      <h2>üìù {tarea.texto}</h2>
      {fechaEntrega && (
        <p><strong>Fecha de entrega:</strong> {fechaEntrega.toLocaleDateString()}</p>
      )}

      {tarea.archivos?.length > 0 && (
        <div className="adjuntos-grid">
          {tarea.archivos.map((archivo, index) => {
            const isImage = archivo.match(/\.(jpg|jpeg|png|gif)$/i);
            const isPDF = archivo.match(/\.pdf$/i);
            const ruta = `http://localhost:3001/storage/${archivo}`;
            return (
              <div key={index} className="adjunto-card">
                {isImage ? (
                  <img src={ruta} alt={`Archivo ${index}`} className="imagen-miniatura" />
                ) : isPDF ? (
                  <iframe src={`${ruta}#toolbar=0`} className="pdf-miniatura" title={`Archivo ${index}`} />
                ) : (
                  <p>Archivo {index + 1}</p>
                )}
                <a href={ruta} target="_blank" rel="noopener noreferrer">Ver archivo</a>
              </div>
            );
          })}
        </div>
      )}

      <div className="tarea-entrega">
        {entrega ? (
          <>
            <p><strong>Entregado:</strong> {fechaReal.toLocaleString()} {entregadoTarde && <span style={{ color: 'orange' }}>(tarde)</span>}</p>
            <p><strong>Calificaci√≥n:</strong> {entrega.calificacion ?? 'Pendiente'}</p>
            <a href={`http://localhost:3001/storage/${entrega.archivo}`} target="_blank" rel="noreferrer">Ver entrega</a>
            {entrega.calificacion === null && (
              <button onClick={deshacerEntrega}>‚ùå Deshacer entrega</button>
            )}
          </>
        ) : (
          <>
            <input
              type="file"
              onChange={(e) => setArchivosEntrega(Array.from(e.target.files))}
              accept=".pdf,image/*"
            />
            <button onClick={entregar}>üì§ Entregar</button>
          </>
        )}
      </div>
    </div>
  );
};

export default TareaDetalleAlumno;
